/* Your SCSS here. */
body {
    font-family: sans-serif;
    margin: 0;
    padding: 0;
}

.container {
    width: 80%;
    margin: 0 auto;
}

#header {
    background: #333;
    color: #fff;
    padding: 20px 0;
    position: fixed;
    width: 100%;
    top: 0;
    z-index: 1000;
    transition: all 0.5s;
}

#navbar .container {
    display: flex;
    justify-content: space-between;
    align-items: center;
}

#navbar .logo {
    font-size: 24px;
    color: #fff;
    text-decoration: none;
    transition: font-size 0.5s;
}

#navbar ul {
    list-style: none;
    margin: 0;
    padding: 0;
    display: flex;
}

#navbar ul li {
    margin-left: 20px;
}

#navbar ul li a {
    color: #fff;
    text-decoration: none;
    font-size: 18px;
    transition: font-size 0.5s;
}

.stripe {
    padding: 100px 0;
    text-align: center;
}

#home {
    background: #f4f4f4;
    padding-top: 140px; /* Adjust for fixed navbar */
}

#footer {
    background: #333;
    color: #fff;
    text-align: center;
    padding: 20px 0;
}

