        if (slideIndex === 0) {
            carouselInner.style.transition = 'none';
            slideIndex = allSlides.length - 2;
            carouselInner.style.transform = `translateX(-${slideIndex * 100}%)`;
        }
        if (slideIndex === allSlides.length - 1) {
            carouselInner.style.transition = 'none';
